-- Updated Sample Recipe Data for Phase 3 Meal Planner\n-- Run this in your Supabase SQL Editor to add sample recipes\n-- Replace 'YOUR_FAMILY_ID' with your actual family_id from the profiles table\n-- Replace 'YOUR_USER_ID' with your actual user_id\n\n-- First, check your family_id and user_id:\n-- SELECT id, family_id FROM profiles WHERE email = 'your_email@example.com';\n\n-- Sample Recipes with updated JSON structure for better compatibility\n\nINSERT INTO recipes (\n  family_id, \n  added_by, \n  name, \n  description, \n  cuisine_type, \n  cooking_method, \n  prep_time, \n  cook_time, \n  servings, \n  difficulty, \n  healthy_rating, \n  ingredients, \n  instructions, \n  dietary_tags, \n  is_favourite,\n  source_type\n) VALUES \n\n-- Classic Spaghetti Bolognese\n(\n  'YOUR_FAMILY_ID',  -- Replace with your family_id\n  'YOUR_USER_ID',    -- Replace with your user_id\n  'Classic Spaghetti Bolognese',\n  'A traditional Italian meat sauce with rich tomato flavour, perfect for family dinners.',\n  'italian',\n  'simmer',\n  15,\n  45,\n  4,\n  'medium',\n  3,\n  '[\n    {\"name\": \"spaghetti\", \"quantity\": \"400\", \"unit\": \"g\"},\n    {\"name\": \"beef mince\", \"quantity\": \"500\", \"unit\": \"g\"},\n    {\"name\": \"onion\", \"quantity\": \"1\", \"unit\": \"large\"},\n    {\"name\": \"carrots\", \"quantity\": \"2\", \"unit\": \"medium\"},\n    {\"name\": \"celery\", \"quantity\": \"2\", \"unit\": \"stalks\"},\n    {\"name\": \"garlic\", \"quantity\": \"3\", \"unit\": \"cloves\"},\n    {\"name\": \"tinned tomatoes\", \"quantity\": \"400\", \"unit\": \"g\"},\n    {\"name\": \"tomato puree\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"red wine\", \"quantity\": \"150\", \"unit\": \"ml\"},\n    {\"name\": \"beef stock\", \"quantity\": \"200\", \"unit\": \"ml\"},\n    {\"name\": \"olive oil\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"parmesan cheese\", \"quantity\": \"50\", \"unit\": \"g\"}\n  ]'::jsonb,\n  '1. Heat olive oil in a large pan and brown the mince, breaking it up with a wooden spoon.\\n2. Add diced onion, carrots, and celery. Cook for 5 minutes until softened.\\n3. Add minced garlic and cook for 1 minute.\\n4. Stir in tomato purée and cook for 2 minutes.\\n5. Add red wine and let it bubble and reduce.\\n6. Add tinned tomatoes and stock, season well.\\n7. Simmer gently for 30-40 minutes, stirring occasionally.\\n8. Cook spaghetti according to package instructions.\\n9. Serve with grated parmesan cheese.',\n  '[]'::jsonb,\n  true,\n  'manual'\n),\n\n-- Quick Chicken Stir Fry\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Quick Chicken Stir Fry',\n  'A healthy and quick weeknight dinner ready in 20 minutes.',\n  'asian',\n  'stir-fry',\n  10,\n  10,\n  4,\n  'easy',\n  5,\n  '[\n    {\"name\": \"chicken breast\", \"quantity\": \"500\", \"unit\": \"g\"},\n    {\"name\": \"broccoli\", \"quantity\": \"200\", \"unit\": \"g\"},\n    {\"name\": \"bell peppers\", \"quantity\": \"2\", \"unit\": \"pieces\"},\n    {\"name\": \"snap peas\", \"quantity\": \"150\", \"unit\": \"g\"},\n    {\"name\": \"spring onions\", \"quantity\": \"4\", \"unit\": \"pieces\"},\n    {\"name\": \"garlic\", \"quantity\": \"2\", \"unit\": \"cloves\"},\n    {\"name\": \"fresh ginger\", \"quantity\": \"1\", \"unit\": \"inch\"},\n    {\"name\": \"soy sauce\", \"quantity\": \"3\", \"unit\": \"tbsp\"},\n    {\"name\": \"sesame oil\", \"quantity\": \"1\", \"unit\": \"tbsp\"},\n    {\"name\": \"vegetable oil\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"rice\", \"quantity\": \"300\", \"unit\": \"g\"}\n  ]'::jsonb,\n  '1. Cook rice according to package instructions.\\n2. Cut chicken into bite-sized pieces and season.\\n3. Heat vegetable oil in a wok or large frying pan.\\n4. Add chicken and stir-fry for 5-6 minutes until cooked through.\\n5. Add garlic and ginger, stir-fry for 30 seconds.\\n6. Add all vegetables and stir-fry for 3-4 minutes until tender-crisp.\\n7. Add soy sauce and sesame oil, toss everything together.\\n8. Serve immediately over rice.',\n  '[\"gluten-free\"]'::jsonb,\n  true,\n  'manual'\n),\n\n-- Vegetarian Lentil Curry\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Hearty Lentil Curry',\n  'A warming and nutritious vegetarian curry packed with protein and flavour.',\n  'indian',\n  'simmer',\n  15,\n  30,\n  6,\n  'easy',\n  5,\n  '[\n    {\"name\": \"red lentils\", \"quantity\": \"250\", \"unit\": \"g\"},\n    {\"name\": \"onion\", \"quantity\": \"1\", \"unit\": \"large\"},\n    {\"name\": \"garlic\", \"quantity\": \"3\", \"unit\": \"cloves\"},\n    {\"name\": \"fresh ginger\", \"quantity\": \"1\", \"unit\": \"inch\"},\n    {\"name\": \"tinned tomatoes\", \"quantity\": \"400\", \"unit\": \"g\"},\n    {\"name\": \"coconut milk\", \"quantity\": \"400\", \"unit\": \"ml\"},\n    {\"name\": \"vegetable stock\", \"quantity\": \"300\", \"unit\": \"ml\"},\n    {\"name\": \"curry powder\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"turmeric\", \"quantity\": \"1\", \"unit\": \"tsp\"},\n    {\"name\": \"cumin\", \"quantity\": \"1\", \"unit\": \"tsp\"},\n    {\"name\": \"spinach\", \"quantity\": \"200\", \"unit\": \"g\"},\n    {\"name\": \"coconut oil\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"basmati rice\", \"quantity\": \"300\", \"unit\": \"g\"}\n  ]'::jsonb,\n  '1. Rinse lentils and cook rice according to package instructions.\\n2. Heat coconut oil in a large pan and sauté diced onion until soft.\\n3. Add minced garlic and ginger, cook for 1 minute.\\n4. Add curry powder, turmeric, and cumin. Cook for 30 seconds.\\n5. Add tinned tomatoes and cook for 5 minutes.\\n6. Add lentils, coconut milk, and stock. Bring to boil.\\n7. Simmer for 20-25 minutes until lentils are tender.\\n8. Stir in spinach and cook until wilted.\\n9. Season with salt and pepper, serve over rice.',\n  '[\"vegetarian\", \"vegan\", \"gluten-free\"]'::jsonb,\n  false,\n  'manual'\n),\n\n-- Easy Salmon with Roasted Vegetables\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Baked Salmon with Roasted Vegetables',\n  'A healthy one-tray dinner that is perfect for busy weeknights.',\n  'british',\n  'roast',\n  10,\n  25,\n  4,\n  'easy',\n  5,\n  '[\n    {\"name\": \"salmon fillets\", \"quantity\": \"4\", \"unit\": \"pieces\"},\n    {\"name\": \"new potatoes\", \"quantity\": \"500\", \"unit\": \"g\"},\n    {\"name\": \"courgettes\", \"quantity\": \"2\", \"unit\": \"pieces\"},\n    {\"name\": \"cherry tomatoes\", \"quantity\": \"200\", \"unit\": \"g\"},\n    {\"name\": \"red onion\", \"quantity\": \"1\", \"unit\": \"pieces\"},\n    {\"name\": \"olive oil\", \"quantity\": \"3\", \"unit\": \"tbsp\"},\n    {\"name\": \"lemon\", \"quantity\": \"1\", \"unit\": \"pieces\"},\n    {\"name\": \"fresh dill\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"garlic\", \"quantity\": \"2\", \"unit\": \"cloves\"}\n  ]'::jsonb,\n  '1. Preheat oven to 200°C/180°C fan.\\n2. Halve new potatoes and toss with 2 tbsp olive oil, salt, and pepper.\\n3. Roast potatoes for 15 minutes.\\n4. Meanwhile, slice courgettes and red onion.\\n5. Add courgettes, onion, and cherry tomatoes to the tray.\\n6. Roast for another 10 minutes.\\n7. Place salmon fillets on the tray, drizzle with remaining oil and lemon juice.\\n8. Season salmon and scatter with dill and minced garlic.\\n9. Roast for 12-15 minutes until salmon flakes easily.\\n10. Serve with lemon wedges.',\n  '[\"gluten-free\", \"dairy-free\"]'::jsonb,\n  true,\n  'manual'\n),\n\n-- Simple Pasta Arrabbiata\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Spicy Pasta Arrabbiata',\n  'A fiery Italian classic with garlic, chilli, and tomatoes - ready in 20 minutes.',\n  'italian',\n  'boil',\n  5,\n  15,\n  4,\n  'easy',\n  3,\n  '[\n    {\"name\": \"penne pasta\", \"quantity\": \"400\", \"unit\": \"g\"},\n    {\"name\": \"tinned tomatoes\", \"quantity\": \"400\", \"unit\": \"g\"},\n    {\"name\": \"garlic\", \"quantity\": \"4\", \"unit\": \"cloves\"},\n    {\"name\": \"red chilli\", \"quantity\": \"2\", \"unit\": \"pieces\"},\n    {\"name\": \"olive oil\", \"quantity\": \"4\", \"unit\": \"tbsp\"},\n    {\"name\": \"fresh parsley\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"parmesan cheese\", \"quantity\": \"50\", \"unit\": \"g\"}\n  ]'::jsonb,\n  '1. Cook pasta according to package instructions until al dente.\\n2. Meanwhile, heat olive oil in a large pan.\\n3. Add sliced garlic and chopped chilli, cook for 1 minute.\\n4. Add tinned tomatoes, season with salt and pepper.\\n5. Simmer for 10-12 minutes until sauce thickens.\\n6. Drain pasta and add to the sauce.\\n7. Toss everything together with chopped parsley.\\n8. Serve with grated parmesan cheese.',\n  '[\"vegetarian\"]'::jsonb,\n  false,\n  'manual'\n),\n\n-- Chicken Tikka Masala\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Creamy Chicken Tikka Masala',\n  'British-Indian favourite with tender chicken in a rich, creamy tomato sauce.',\n  'indian',\n  'simmer',\n  20,\n  25,\n  4,\n  'medium',\n  4,\n  '[\n    {\"name\": \"chicken breast\", \"quantity\": \"600\", \"unit\": \"g\"},\n    {\"name\": \"onion\", \"quantity\": \"1\", \"unit\": \"large\"},\n    {\"name\": \"garlic\", \"quantity\": \"3\", \"unit\": \"cloves\"},\n    {\"name\": \"fresh ginger\", \"quantity\": \"1\", \"unit\": \"inch\"},\n    {\"name\": \"tikka masala paste\", \"quantity\": \"3\", \"unit\": \"tbsp\"},\n    {\"name\": \"tinned tomatoes\", \"quantity\": \"400\", \"unit\": \"g\"},\n    {\"name\": \"double cream\", \"quantity\": \"150\", \"unit\": \"ml\"},\n    {\"name\": \"natural yogurt\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"vegetable oil\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"basmati rice\", \"quantity\": \"300\", \"unit\": \"g\"},\n    {\"name\": \"fresh coriander\", \"quantity\": \"2\", \"unit\": \"tbsp\"}\n  ]'::jsonb,\n  '1. Cut chicken into chunks and marinate with 1 tbsp tikka paste and yogurt for 15 minutes.\\n2. Cook rice according to package instructions.\\n3. Heat oil in a large pan and cook chicken until golden. Remove and set aside.\\n4. In the same pan, cook diced onion until soft.\\n5. Add garlic, ginger, and remaining tikka paste. Cook for 1 minute.\\n6. Add tinned tomatoes and simmer for 10 minutes.\\n7. Return chicken to pan, add cream and simmer for 5 minutes.\\n8. Season and garnish with fresh coriander.\\n9. Serve with basmati rice.',\n  '[]'::jsonb,\n  true,\n  'manual'\n);\n\n-- After inserting, you can verify with:\n-- SELECT name, cuisine_type, difficulty, healthy_rating FROM recipes ORDER BY created_at DESC;