-- Quick setup: Sample breakfast and lunch recipes for testing\n-- Replace YOUR_FAMILY_ID and YOUR_USER_ID with your actual values\n-- Find these with: SELECT id, family_id FROM profiles WHERE email = 'your_email@example.com';\n\nINSERT INTO recipes (\n  family_id, \n  added_by, \n  name, \n  description, \n  cuisine_type, \n  cooking_method, \n  prep_time, \n  cook_time, \n  servings, \n  difficulty, \n  healthy_rating, \n  ingredients, \n  instructions, \n  dietary_tags, \n  is_favourite,\n  source_type\n) VALUES \n\n-- BREAKFAST RECIPES\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Scrambled Eggs on Toast',\n  'Quick and easy breakfast with buttery scrambled eggs.',\n  'british',\n  'fry',\n  2,\n  5,\n  2,\n  'easy',\n  4,\n  '[\n    {\"name\": \"eggs\", \"quantity\": \"4\", \"unit\": \"pieces\"},\n    {\"name\": \"butter\", \"quantity\": \"20\", \"unit\": \"g\"},\n    {\"name\": \"milk\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"bread\", \"quantity\": \"4\", \"unit\": \"slices\"}\n  ]'::jsonb,\n  '1. Toast the bread slices.\\n2. Beat eggs with milk, salt, and pepper.\\n3. Melt butter in a non-stick pan over low heat.\\n4. Add eggs and stir gently until just set.\\n5. Serve immediately on toast.',\n  '[\"vegetarian\"]'::jsonb,\n  false,\n  'manual'\n),\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Greek Yogurt with Berries',\n  'Healthy breakfast with protein and fresh fruit.',\n  'mediterranean',\n  'no-cook',\n  3,\n  0,\n  1,\n  'easy',\n  5,\n  '[\n    {\"name\": \"greek yogurt\", \"quantity\": \"200\", \"unit\": \"g\"},\n    {\"name\": \"mixed berries\", \"quantity\": \"100\", \"unit\": \"g\"},\n    {\"name\": \"honey\", \"quantity\": \"1\", \"unit\": \"tbsp\"},\n    {\"name\": \"granola\", \"quantity\": \"30\", \"unit\": \"g\"}\n  ]'::jsonb,\n  '1. Place Greek yogurt in a bowl.\\n2. Top with mixed berries and granola.\\n3. Drizzle with honey and serve.',\n  '[\"vegetarian\", \"gluten-free\"]'::jsonb,\n  true,\n  'manual'\n),\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Avocado Toast',\n  'Trendy and nutritious breakfast with smashed avocado.',\n  'modern',\n  'no-cook',\n  5,\n  2,\n  2,\n  'easy',\n  5,\n  '[\n    {\"name\": \"bread\", \"quantity\": \"2\", \"unit\": \"slices\"},\n    {\"name\": \"avocado\", \"quantity\": \"1\", \"unit\": \"pieces\"},\n    {\"name\": \"lemon\", \"quantity\": \"0.5\", \"unit\": \"pieces\"},\n    {\"name\": \"salt\", \"quantity\": \"1\", \"unit\": \"pinch\"},\n    {\"name\": \"black pepper\", \"quantity\": \"1\", \"unit\": \"pinch\"}\n  ]'::jsonb,\n  '1. Toast the bread until golden.\\n2. Mash avocado with lemon juice, salt, and pepper.\\n3. Spread avocado mixture on toast.\\n4. Serve immediately.',\n  '[\"vegetarian\", \"vegan\"]'::jsonb,\n  true,\n  'manual'\n),\n\n-- LUNCH RECIPES\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Ham and Cheese Sandwich',\n  'Classic lunch sandwich with fresh ingredients.',\n  'british',\n  'no-cook',\n  5,\n  0,\n  1,\n  'easy',\n  3,\n  '[\n    {\"name\": \"bread\", \"quantity\": \"2\", \"unit\": \"slices\"},\n    {\"name\": \"ham\", \"quantity\": \"80\", \"unit\": \"g\"},\n    {\"name\": \"cheese\", \"quantity\": \"30\", \"unit\": \"g\"},\n    {\"name\": \"butter\", \"quantity\": \"10\", \"unit\": \"g\"},\n    {\"name\": \"lettuce\", \"quantity\": \"2\", \"unit\": \"leaves\"},\n    {\"name\": \"tomato\", \"quantity\": \"2\", \"unit\": \"slices\"}\n  ]'::jsonb,\n  '1. Butter the bread slices.\\n2. Layer ham, cheese, lettuce, and tomato.\\n3. Close sandwich and cut diagonally if desired.',\n  '[]'::jsonb,\n  false,\n  'manual'\n),\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Caesar Salad',\n  'Fresh and crispy salad with homemade dressing.',\n  'mediterranean',\n  'no-cook',\n  10,\n  0,\n  2,\n  'easy',\n  4,\n  '[\n    {\"name\": \"romaine lettuce\", \"quantity\": \"1\", \"unit\": \"head\"},\n    {\"name\": \"parmesan cheese\", \"quantity\": \"50\", \"unit\": \"g\"},\n    {\"name\": \"croutons\", \"quantity\": \"50\", \"unit\": \"g\"},\n    {\"name\": \"caesar dressing\", \"quantity\": \"3\", \"unit\": \"tbsp\"},\n    {\"name\": \"lemon\", \"quantity\": \"0.5\", \"unit\": \"pieces\"}\n  ]'::jsonb,\n  '1. Wash and chop romaine lettuce.\\n2. Toss with Caesar dressing.\\n3. Top with croutons and grated Parmesan.\\n4. Serve with lemon wedges.',\n  '[\"vegetarian\"]'::jsonb,\n  true,\n  'manual'\n),\n(\n  'YOUR_FAMILY_ID',\n  'YOUR_USER_ID',\n  'Tomato Soup',\n  'Comforting homemade tomato soup perfect for lunch.',\n  'british',\n  'simmer',\n  5,\n  20,\n  4,\n  'easy',\n  4,\n  '[\n    {\"name\": \"tinned tomatoes\", \"quantity\": \"800\", \"unit\": \"g\"},\n    {\"name\": \"onion\", \"quantity\": \"1\", \"unit\": \"pieces\"},\n    {\"name\": \"garlic\", \"quantity\": \"2\", \"unit\": \"cloves\"},\n    {\"name\": \"vegetable stock\", \"quantity\": \"500\", \"unit\": \"ml\"},\n    {\"name\": \"double cream\", \"quantity\": \"100\", \"unit\": \"ml\"},\n    {\"name\": \"olive oil\", \"quantity\": \"2\", \"unit\": \"tbsp\"},\n    {\"name\": \"fresh basil\", \"quantity\": \"2\", \"unit\": \"tbsp\"}\n  ]'::jsonb,\n  '1. Heat oil in a large pan and saut√© diced onion until soft.\\n2. Add minced garlic and cook for 1 minute.\\n3. Add tinned tomatoes and stock, bring to boil.\\n4. Simmer for 15 minutes, then blend until smooth.\\n5. Stir in cream and fresh basil before serving.',\n  '[\"vegetarian\", \"gluten-free\"]'::jsonb,\n  false,\n  'manual'\n);\n\n-- Verify insertion\nSELECT name, cuisine_type, cooking_method FROM recipes \nWHERE name IN ('Scrambled Eggs on Toast', 'Greek Yogurt with Berries', 'Ham and Cheese Sandwich', 'Caesar Salad') \nORDER BY created_at DESC;